def kth_smallest(list1, list2, k):
    def find_kth(start1, end1, start2, end2, k):
        if start1 >= end1:
            return list2[start2 + k]
        if start2 >= end2:
            return list1[start1 + k]
        mid1 = (start1 + end1) // 2
        mid2 = (start2 + end2) // 2
        if list1[mid1] <= list2[mid2]:
            if k < mid1 - start1 + mid2 - start2 + 1:
                return find_kth(start1, end1, start2, mid2, k)
            else:
                return find_kth(mid1 + 1, end1, start2, end2, k - mid1 + start1 - 1)
        else:
            if k < mid1 - start1 + mid2 - start2 + 1:
                return find_kth(start1, mid1, start2, end2, k)
            else:
                return find_kth(start1, end1, mid2 + 1, end2, k - mid2 + start2 - 1)

    return find_kth(0, len(list1), 0, len(list2), k - 1)

