def binomial_coefficient(n, k):
    if (k > n - k):
        k = n - k
    res = 1
    for x in range(k):
        res = res * (n - x)
        res = res // (x + 1)
    return res

def count_trees(n):
    if (n <= 1):
        return 1
    else:
        total = binomial_coefficient(2*n, n)
        return total // (n + 1)
