def findKthSmallest(nums1, nums2, k):
    m, n = len(nums1), len(nums2)
    if m > n:
        return findKthSmallest(nums2, nums1, k)
    if not nums1:
        return nums2[k - 1]
    if k == 1:
        return min(nums1[0], nums2[0])
    i = min(m, k // 2)
    j = min(n, k // 2)
    if nums1[i - 1] > nums2[j - 1]:
