```cpp
#include<iostream>
using namespace std;

int fibonacci(int n) {
   if(n <= 1)
      return n;
   else
      return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
   int n;
   cout << "Unesite poziciju Fibonaccijevog broja koji želite pronaći: ";
   cin >> n;
   cout << "Fibonaccijev broj na poziciji " << n << " je " << fibonacci(n);
   return 0;
}
```

