class Node:
    def __init__(self, key):
        self.key = key
        self.children = []

def najvisji_skupni_prednik(root, n1, n2):
    if root is None:
        return None

    if root.key == n1 or root.key == n2:
        return root

    matching_nodes = [najvisji_skupni_prednik(child, n1, n2) for child in root.children]
    matching_nodes = [node for node in matching_nodes if node is not None]

    if len(matching_nodes) > 1:
        return root
    elif matching_nodes:
        return matching_nodes[0]
    else:
        return None

